type variants =
  | Var1 of int
  | Var2
  | Var3 of (string * int)
  [@@deriving (Bson_ext)]

type variants_poly = [
  | `VarP1 of int
  | `VarP2
  | `VarP3 of (string * int)
 ] [@@deriving (Bson_ext)]


